import React, { useEffect, useState } from "react";
import axios from "../utils/axiosInstance";
import { Link } from "react-router-dom";

const Orders = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const res = await axios.get("/api/orders", { withCredentials: true });
        setOrders(res.data);
      } catch (err) {
        console.error("Error fetching orders:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchOrders();
  }, []);

  if (loading) {
    return (
      <div className="text-center mt-5">
        <div className="spinner-border text-primary" role="status" />
        <p className="mt-2">Loading your orders...</p>
      </div>
    );
  }

  if (orders.length === 0) {
    return (
      <div className="text-center mt-5">
        <h5 className="text-muted">You haven't placed any orders yet.</h5>
        <Link to="/" className="btn btn-outline-primary mt-3">
          Start Trading
        </Link>
      </div>
    );
  }

  return (
    <div className="container mt-4">
      <h3 className="mb-4">ðŸ§¾ Your Recent Orders</h3>

      <div className="table-responsive">
        <table className="table table-striped table-hover border shadow-sm">
          <thead className="table-dark">
            <tr>
              <th>Stock</th>
              <th>Action</th>
              <th>Qty</th>
              <th>Price</th>
              <th>Value</th>
              <th>Time</th>
            </tr>
          </thead>
          <tbody>
            {orders.map((order, idx) => {
              const isBuy = order.mode === "BUY";
              return (
                <tr key={idx}>
                  <td>{order.name}</td>
                  <td>
                    <span
                      className={`badge rounded-pill ${
                        isBuy ? "bg-success" : "bg-danger"
                      }`}>
                      {order.mode}
                    </span>
                  </td>
                  <td>{order.qty}</td>
                  <td>â‚¹{order.price.toFixed(2)}</td>
                  <td>â‚¹{(order.price * order.qty).toFixed(2)}</td>
                  <td>
                    {new Date(order.createdAt).toLocaleString("en-IN", {
                      hour12: true,
                      year: "numeric",
                      month: "short",
                      day: "numeric",
                      hour: "numeric",
                      minute: "numeric",
                    })}
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Orders;
