import React, { useState, useEffect, useContext } from "react";
import { Link } from "react-router-dom";
import axios from "../utils/axiosInstance"; // your configured axios instance
import GeneralContext from "./GeneralContext";
import "./BuyActionWindow.css";

const BuyActionWindow = ({ uid, mode, onOrderPlaced }) => {
  const [stockQuantity, setStockQuantity] = useState(1);
  const [stockPrice, setStockPrice] = useState(0.0);
  const { closeBuyWindow } = useContext(GeneralContext);
  const { refreshAll } = useContext(GeneralContext);

  useEffect(() => {
    const fetchPrice = async () => {
      try {
        const res = await axios.get(`/api/price/${uid}.NS`);
        if (res.data?.price) {
          setStockPrice(res.data.price);
        }
      } catch (err) {
        console.error("Price fetch error:", err);
      }
    };
    fetchPrice();
  }, [uid]);

  const handleSubmit = async () => {
    try {
      await axios.post("http://localhost:3002/newOrder", {
        name: uid,
        qty: Number(stockQuantity),
        price: Number(stockPrice),
        mode: mode || "BUY",
      });
      alert(`${mode} order placed!`);
      if (onOrderPlaced) onOrderPlaced(); // ✅ Refresh context
      closeBuyWindow();
    } catch (err) {
      console.error("Order failed:", err);
      alert("Order failed");
    }
  };

  return (
    <div className="container" id="buy-window" draggable="true">
      <div className="regular-order">
        <div className="inputs">
          <fieldset>
            <legend>Qty.</legend>
            <input
              type="number"
              value={stockQuantity}
              min={1}
              onChange={(e) => setStockQuantity(e.target.value)}
            />
          </fieldset>

          <fieldset>
            <legend>Price</legend>
            <input
              type="number"
              step="0.05"
              value={stockPrice}
              onChange={(e) => setStockPrice(e.target.value)}
            />
          </fieldset>
        </div>
      </div>

      <div className="buttons">
        <span>Margin required ₹{(stockQuantity * stockPrice).toFixed(2)}</span>
        <div>
          <Link
            className={`btn ${mode === "BUY" ? "btn-blue" : "btn-red"}`}
            onClick={handleSubmit}>
            {mode === "BUY" ? "Buy" : "Sell"}
          </Link>
          <Link to="#" className="btn btn-grey" onClick={closeBuyWindow}>
            Cancel
          </Link>
        </div>
      </div>
    </div>
  );
};

export default BuyActionWindow;
