import React, { useEffect, useState } from "react";
import { Line } from "react-chartjs-2";
import axios from "../utils/axiosInstance";
import {
  Chart as ChartJS,
  LineElement,
  PointElement,
  LinearScale,
  CategoryScale,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(
  LineElement,
  PointElement,
  LinearScale,
  CategoryScale,
  Tooltip,
  Legend
);

const PortfolioPerformance = () => {
  const [dataPoints, setDataPoints] = useState([]);

  useEffect(() => {
    const fetchPortfolioSnapshot = async () => {
      try {
        const res = await axios.get("/api/holdings");
        const holdings = res.data;
        let total = 0;

        for (let stock of holdings) {
          let symbol = stock.name;
          if (!symbol.endsWith(".NS") && !symbol.endsWith(".BO")) {
            symbol += ".NS";
          }

          try {
            const priceRes = await axios.get(`/api/price/${symbol}`);
            const ltp = parseFloat(priceRes.data.price || 0);
            total += ltp * stock.qty;
          } catch {
            continue;
          }
        }

        const now = new Date();
        const label = now.toLocaleTimeString([], {
          hour: "2-digit",
          minute: "2-digit",
        });

        setDataPoints((prev) => [
          ...prev.slice(-19),
          { time: label, value: total },
        ]); // Keep last 20
      } catch (err) {
        console.error("Error fetching portfolio performance", err);
      }
    };

    fetchPortfolioSnapshot();
    const interval = setInterval(fetchPortfolioSnapshot, 10000); // every 10s

    return () => clearInterval(interval);
  }, []);

  const chartData = {
    labels: dataPoints.map((point) => point.time),
    datasets: [
      {
        label: "Portfolio Value (₹)",
        data: dataPoints.map((point) => point.value),
        fill: true,
        backgroundColor: "rgba(34,197,94,0.1)",
        borderColor: "#10b981",
        tension: 0.4,
        pointRadius: 1.5,
      },
    ],
  };

  const options = {
    responsive: true,
    plugins: {
      legend: { display: false },
    },
    scales: {
      x: {
        title: { display: true, text: "Time" },
        ticks: { color: "#64748b" },
      },
      y: {
        title: { display: true, text: "₹ Value" },
        ticks: { color: "#64748b" },
      },
    },
  };

  return (
    <div className="section">
      <span>
        <p>Portfolio Performance</p>
      </span>
      <div style={{ height: "300px" }}>
        <Line data={chartData} options={options} />
      </div>
    </div>
  );
};

export default PortfolioPerformance;
